import { TouchableOpacity } from 'react-native';
import React from 'react'
import { Ionicons } from '@expo/vector-icons';
import { useLoading } from '../Context';

export default function ArrowBack({ navigation, navigate, style }) {
    const { setShouldRefresh } = useLoading();

    let btnStyle = {
        marginHorizontal: 10,
        position: 'absolute',
        zIndex: 99,
        ...style
    }

    handleNav = () => {
        if(navigate == 'Profile'){
            setShouldRefresh(true);
        }
        navigation.navigate(navigate);
    }

    return (
        <TouchableOpacity style={btnStyle} onPress={handleNav}>
            <Ionicons name="arrow-back-circle-outline" size={30} color="darkblue" />
        </TouchableOpacity>
    )
}

import { TouchableOpacity } from 'react-native';
import { View, Text, TextInput, StyleSheet } from 'react-native';
import Modal from 'react-native-modal';
import { useState, useEffect } from 'react';
import { Ionicons } from '@expo/vector-icons';
import Storage from './../database/firebaseMethods';
import { useLoading } from './../Context';
import AsyncStorage from '@react-native-async-storage/async-storage';


const darkBlue = '#00008b';


export default function ChangeData({ label, value, refresh }) {

  const { showLoading, hideLoading, setShouldRefresh } = useLoading();

  const [isModalVisible, setModalVisible] = useState(false);
  const [name, setname] = useState('');
  const [email, setemail] = useState('');
  const [perfilInv, setperfilInv] = useState('');
  const [profissao, setprofissao] = useState('');

  const [userData, setUserData] = useState({});

  const toggleModal = () => {
    setModalVisible(!isModalVisible);
  };

  useEffect(() => {
    const getAllData = async () => {
      const doc = await AsyncStorage.getItem('userDoc');
      const fr = new Storage(doc);
      try {
        showLoading();
        let res = await fr.getFullDoc();
        let uD = res.userData;
        setUserData(uD);
      } catch (err) { console.log(err) } finally { hideLoading(); }
    };

    getAllData().catch(console.error);
  }, []);

  updateData = async () => {
    const doc = await AsyncStorage.getItem('userDoc');
    const fr = new Storage(doc);

    if (label === 'Nome') {
      let out = await updateName(fr);
      console.log(out);
    }
    if (label === 'Email') {
      let out = await updateEmail(fr);
      console.log(out);
    }
    if (label === 'Profissão') {
      let out = await updateProfissao(fr);
      console.log(out);
    }
    if (label === 'Perfil de Investimento') {
      let out = await updatePerfil(fr);
      console.log(out);
    }

    toggleModal();
    setShouldRefresh(true);
    await refresh();
  };



  updateName = async (fr) => {
    let outp = await fr.updateDoc({
      userData: {
        name: name,
        email: userData.email,
        perfilInvestimento: userData.perfilInvestimento,
        profissao: userData.profissao,
      },
    });
    return outp;
  }

  updateEmail = async (fr) => {
    let outp = await fr.updateDoc({
      userData: {
        name: userData.name,
        email: email,
        perfilInvestimento: userData.perfilInvestimento,
        profissao: userData.profissao,
      },
    });
    return outp;
  }

  updateProfissao = async (fr) => {
    let outp = await fr.updateDoc({
      userData: {
        name: userData.name,
        email: userData.email,
        perfilInvestimento: userData.perfilInvestimento,
        profissao: profissao,
      },
    });
    return outp;
  }

  updatePerfil = async (fr) => {
    let outp = await fr.updateDoc({
      userData: {
        name: userData.name,
        email: userData.email,
        perfilInvestimento: perfilInv,
        profissao: userData.profissao,
      },
    });
    return outp;
  }




  return (
    <View>
      <View style={styles.component}>
        <Modal style={styles.modal_modal} isVisible={isModalVisible}>
          <View style={styles.modal_modalView}>
            <View style={styles.modal_container}>
              <View style={styles.modal_titleView}>
                <Text style={styles.modal_title}>Alterando</Text>
              </View>

              <View style={{ padding: 12 }}>
                <View style={styles.modal_inputGroup}>
                  <Text style={styles.modal_label}>{label}</Text>
                  <TextInput
                    style={styles.modal_input}
                    placeholder={value}
                    onChangeText={(text) => {
                      if (label === 'Nome') {
                        setname(text);
                      }
                      if (label === 'Email') {
                        setemail(text)
                      }
                      if (label === 'Profissão') {
                        setprofissao(text)
                      }
                      if (label === 'Perfil de Investimento') {
                        setperfilInv(text)
                      }
                    }}
                  />
                </View>
              </View>

              <View style={styles.modal_buttonContainer}>
                <TouchableOpacity
                  style={styles.modal_btn}
                  onPress={toggleModal}>
                  <Ionicons
                    name="arrow-back-circle-sharp"
                    size={55}
                    color={darkBlue}
                  />
                </TouchableOpacity>

                <TouchableOpacity style={styles.modal_btn} onPress={updateData}>
                  <Ionicons
                    name="add-circle-sharp"
                    size={55}
                    color={darkBlue}
                  />
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </Modal>

        <View style={{ flexDirection: 'column' }}>
          <Text style={styles.label}>{label}</Text>
          <Text style={styles.value}>{value}</Text>
        </View>
        <TouchableOpacity onPress={toggleModal} style={styles.btn}>
          <Text>Alterar</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.border} />
    </View>
  );
}

const styles = StyleSheet.create({
  border: {
    borderBottomColor: darkBlue,
    borderBottomWidth: 1,
    marginLeft: 20,
    marginRight: 20,
    marginBottom: 5,
    marginTop: 5,
  },
  component: {
    marginTop: 10,
    padding: 18,
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  label: {
    fontSize: 12,
    paddingLeft: 20,
    color: '#c5c5c5',
  },
  value: {
    fontSize: 18,
    paddingLeft: 20,
    paddingRight: 20,
  },
  btn: {
    alignSelf: 'center',
    paddingTop: 8,
    paddingBottom: 8,
    paddingLeft: 12,
    paddingRight: 12,
    borderRadius: 10,
    fontSize: 18,
    backgroundColor: '#e7e7e7',
  },

  modal_modalView: {
    backgroundColor: 'white',
    borderRadius: 15,
    margin: 10,
  },
  modal_container: {
    borderTopLeftRadius: 20,
  },
  modal_titleView: {
    backgroundColor: darkBlue,
    marginBottom: 5,
  },
  modal_title: {
    fontSize: 20,
    fontWeight: 'bold',
    padding: 20,
    textAlign: 'center',
    color: '#fff',
  },
  modal_inputGroup: {
    marginTop: 10,
    marginBottom: 10,
  },
  modal_label: {
    textAlign: 'left',
    fontSize: 20,
    marginBottom: 8,
  },
  modal_input: {
    borderWidth: 0.8,
    borderColor: darkBlue,
    borderRadius: 5,
    paddingHorizontal: 8,
    paddingVertical: 5,
    marginBottom: 16,
    fontSize: 20,
  },
  modal_buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 15,
  },
});

import React, { useEffect, useState } from 'react';
import { SafeAreaView, View, Text, TouchableOpacity, StyleSheet, TextInput, } from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import { AntDesign, Ionicons, Fontisto } from '@expo/vector-icons';
import Modal from 'react-native-modal';
import Storage from './../database/firebaseMethods';
import { useLoading } from './../Context';
import AsyncStorage from '@react-native-async-storage/async-storage';


export default function HorizontalMenu({ refresh, navigation, dados, allData }) {
  const { showLoading, hideLoading, setShouldRefresh } = useLoading();


  const [myDados, setDados] = useState([]);
  useEffect(() => {
    setDados(dados);
  }, [dados]);

  const [description, setDescription] = useState('');
  const [money, setMoney] = useState(0);

  //#region DATE

  const [date, setDate] = useState(new Date());
  const [mode, setMode] = useState('date');
  const [show, setShow] = useState(false);

  const onChange = (event, selectedDate) => {
    const currentDate = selectedDate;
    setShow(false);
    setDate(currentDate);
  };

  const showMode = (currentMode) => {
    setShow(true);
    setMode(currentMode);
  };

  const showDatepicker = () => {
    showMode('date');
  };

  const formatarData = (data) => {
    const dia = String(data.getDate()).padStart(2, '0');
    const mes = String(data.getMonth() + 1).padStart(2, '0'); // Mês é base 0, então adicione 1
    const ano = data.getFullYear();

    return `${dia}/${mes}/${ano}`;
  };

  //#endregion

  const [isModalVisible, setModalVisible] = useState(false);
  const handleModal = () => {
    if (dados.type === 2) {
      navigation.navigate('Settings');
    } else if (dados.type === 3) {
      navigation.navigate('Graphs', { allData: allData });
    } else if (dados.type === 4) {
      navigation.navigate('Money');
    } else {
      setModalVisible(!isModalVisible);
    }
  };

  const saveData = async () => {
    try {
      const doc = await AsyncStorage.getItem('userDoc');
      const fr = new Storage(doc);
      setModalVisible(false);
      showLoading();
      if (myDados.type === 0) {
        res = await fr.appendToIncomes({
          description: description,
          value: money,
          date: formatarData(date),
          category: dados.label,
        });
      } else if (myDados.type === 1) {
        res = await fr.appendToExpenses({
          description: description,
          value: money,
          date: formatarData(date),
          category: dados.label,
        });
      }
    } catch (err) {
      console.log(err);
      alert(err);
    } finally {
      setShouldRefresh(true);
      hideLoading();
    }

    refresh();
  };

  return (
    <View>
      <Modal style={st.modal} isVisible={isModalVisible}>
        <View style={st.modalView}>
          <View style={st.container}>
            <View style={st.titleView}>
              <Text style={st.title}>
                {dados.actionType} {dados.label}
              </Text>
            </View>

            <View style={{ padding: 12 }}>
              <View style={st.inputGroup}>
                <Text style={st.label}>{dados.label}</Text>
                <TextInput
                  style={st.input}
                  onChangeText={(text) => setDescription(text)}
                />
              </View>
              <View style={st.inputGroup}>
                <Text style={st.label}>Valor (R$)</Text>
                <TextInput
                  style={st.input}
                  keyboardType="numeric"
                  onChangeText={(text) => {
                    const formattedText = text.replace(/[^0-9.]/g, '');
                    setMoney(formattedText);
                  }}
                />
              </View>

              <SafeAreaView>
                <Text style={st.label}>Date</Text>
                <TouchableOpacity onPress={showDatepicker}>
                  <Text style={st.input}>{formatarData(date)}</Text>
                </TouchableOpacity>

                {show && (
                  <DateTimePicker
                    testID="dateTimePicker"
                    value={date}
                    mode={mode}
                    is24Hour={true}
                    onChange={onChange}
                  />
                )}
              </SafeAreaView>
            </View>

            <View style={st.buttonContainer}>
              <TouchableOpacity style={st.btn} onPress={handleModal}>
                <Ionicons
                  name="arrow-back-circle-sharp"
                  size={55}
                  color="darkblue"
                />
              </TouchableOpacity>
              <TouchableOpacity style={st.btn} onPress={saveData}>
                <Ionicons name="add-circle-sharp" size={55} color="darkblue" />
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>

      <TouchableOpacity style={st._actionBtn} onPress={handleModal}>
        <View style={st._areaBtn}>
          {dados.type === 4 ?
            <Fontisto name={dados.icon} size={26} color="#000" /> :
            <AntDesign name={dados.icon} size={26} color="#000" />}
        </View>
        <Text style={st._labelBtn}>{dados.label}</Text>
      </TouchableOpacity>
    </View>
  );
}

const st = StyleSheet.create({
  _actionBtn: {
    alignItems: 'center',
    marginRight: 32,
  },
  _areaBtn: {
    backgroundColor: '#ecf0f1',
    borderRadius: 30,
    height: 60,
    width: 60,
    justifyContent: 'center',
    alignItems: 'center',
  },
  _labelBtn: {
    marginTop: 4,
    textAlign: 'center',
    fontWeight: 'bold',
  },

  modalView: {
    backgroundColor: 'white',
    borderRadius: 15,
    margin: 10,
  },
  container: {
    borderTopLeftRadius: 20,
  },
  titleView: {
    backgroundColor: 'darkblue',
    marginBottom: 5,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    padding: 20,
    textAlign: 'center',
    color: '#fff',
  },
  inputGroup: {
    marginTop: 10,
    marginBottom: 10,
  },
  label: {
    textAlign: 'left',
    fontSize: 20,
    marginBottom: 8,
  },
  input: {
    borderWidth: 0.8,
    borderColor: 'darkblue',
    borderRadius: 5,
    paddingHorizontal: 8,
    paddingVertical: 5,
    marginBottom: 16,
    fontSize: 20,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 15,
  },
});

import React, { useEffect, useState } from 'react';
import { View, Animated, Easing } from 'react-native';

export default function LogoImage({ navigation }) {
    const [fadeAnim] = useState(new Animated.Value(0));
    const [rotation] = useState(new Animated.Value(0));

    useEffect(() => {
        const startAnimation = async () => {
            Animated.timing(fadeAnim, {
                toValue: 1,
                duration: 5000,
                easing: Easing.linear,
                useNativeDriver: true,
            }).start();

            await new Promise(resolve => setTimeout(resolve, 3500));

            Animated.timing(rotation, {
                toValue: 1,
                duration: 2000,
                easing: Easing.linear,
                useNativeDriver: true,
            }).start();

            await new Promise(resolve => setTimeout(resolve, 2000));
        };

        startAnimation();
    }, []);

    const animatedStyle = {
        opacity: fadeAnim,
        transform: [
            {
                rotate: rotation.interpolate({
                    inputRange: [0, 1],
                    outputRange: ['0deg', '360deg'],
                }),
            },
        ],
    };

    return (
        <View style={{ justifyContent: 'center', alignItems: 'center' }}>
            <Animated.Image
                source={require('./../static/imgs/logo.png')}
                style={[{ width: 200, height: 200 }, animatedStyle]}
            />
        </View>
    );
}

import { BarChart } from "react-native-chart-kit";
import { Dimensions, View, StyleSheet, Text } from "react-native";

const screenWidth = Dimensions.get("window").width;

export default function MoneysGraph({ bardata, name, color }) {

    const chartConfig = {
        backgroundGradientFrom: "#fff",
        backgroundGradientFromOpacity: 0,
        backgroundGradientTo: "#fff",
        backgroundGradientToOpacity: 0,
        strokeWidth: 1,
        barPercentage: 0.5,
        color: () => { return color }
    };


    return (
        <View style={styles.graphGroup}>
            <Text style={styles.txt}>{name.replace("/", " x ")}</Text>
            <BarChart
                width={screenWidth - 20}
                height={220}
                data={bardata}
                yAxisLabel=""
                chartConfig={chartConfig}
                fromZero={true}
                showValuesOnTopOfBars={true}
            />
        </View>
    )
}


const styles = StyleSheet.create({
    txt: {
        textAlign: 'center',
        fontSize: 20,
        fontWeight: '500'
    },
    graphGroup: {
        borderWidth: 1,
        borderColor: 'rgba(0, 0, 139,.2)',
        margin: 10,
        padding: 5
    }
})

import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { React, useState } from 'react';

export default function Movements({ props }) {
  const [showValue, setShowValue] = useState(false);


  function formatarData(dataStr) {
    if (dataStr) {
      return dataStr;
    } else {
      return 'sem data';
    }
  }

  return (
    <TouchableOpacity
      style={st.container}
      onPress={() => setShowValue(!showValue)}>
      <Text style={st.data}>{formatarData(props.date)}</Text>
      <View style={st.content}>
        <Text style={st.label}>{props.description}</Text>

        {showValue ? (
          <View exitBeforeEnter>
            <Text
              style={props.type === 1 ? st.value : st.expenses}
              from={{ translateX: 100 }}
              animate={{ translateX: 0 }}
              transition={{ type: 'timing', duration: 500 }}>
              {props.type === 1 ? `R$ ${parseFloat(props.value).toFixed(2)}` : `R$ -${parseFloat(props.value).toFixed(2)}`}
            </Text>
          </View>
        ) : (
          <View style={st.skeleton} />
        )}
      </View>
    </TouchableOpacity>
  );
}

const st = StyleSheet.create({
  container: {
    flex: 1,
    marginBottom: 24,
    borderBottomWidth: 0.5,
    padding: 5,
    borderBottomColor: '#dadada',
  },
  content: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 2,
    marginBottom: 8,
  },
  data: {
    color: '#dadada',
    fontWeight: 'bold',
  },
  label: {
    fontWeight: 'bold',
    fontSize: 16,
  },
  value: {
    fontSize: 15,
    color: '#2ecc71',
    fontWeight: 'bold',
  },
  expenses: {
    fontSize: 15,
    color: '#e74c3c',
    fontWeight: 'bold',
  },
  skeleton: {
    marginTop: 6,
    width: 80,
    height: 10,
    backgroundColor: '#dadada',
    borderRadius: 8,
  },
});

import React, { useState, createContext, useContext } from 'react';
import { View, ActivityIndicator, StyleSheet } from 'react-native';

const LoadingContext = createContext();

export const LoadingProvider = ({ children }) => {
    const [loading, setLoading] = useState(false);
    const [shouldRef, setShouldRef] = useState(true)

    const showLoading = () => setLoading(true);
    const hideLoading = () => setLoading(false);


    const getShouldRefresh = () => shouldRef;
    const setShouldRefresh = (should) => setShouldRef(should);

    return (
        <LoadingContext.Provider value={{
            showLoading, hideLoading,
            getShouldRefresh, setShouldRefresh
        }}>
            {children}
            {loading && <LoadingIndicator />}
        </LoadingContext.Provider>
    );
};

export const useLoading = () => {
    const context = useContext(LoadingContext);
    if (!context) {
        throw new Error('useLoading must be used within a LoadingProvider');
    }
    return context;
};

const LoadingIndicator = () => (
    <View style={styles.container}>
        <ActivityIndicator size="large" color="#0000ff" />
    </View>
);

const styles = StyleSheet.create({
    container: {
        ...StyleSheet.absoluteFillObject,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
    },
});

import { initializeApp } from 'firebase/app';
import { getFirestore } from 'firebase/firestore';


/**
 * O arquivo fornece a configuracao e a instancia do Firebase para serem utilizadas em um aplicativo React Native 
 * A configuracao inclui as credenciais da aplicacao Firebase e a inicializacao do aplicativo e do Firestore
 */


/**
 * Um objeto que contem as credenciais e configuracoes do projeto Firebase
 */
const firebaseConfig = {
    //configs
};

/**
 *  A instancia inicializada do aplicativo Firebase
 */
const app = initializeApp(firebaseConfig);

/**
 * A instancia do Firestore associada ao aplicativo Firebase
 */
const db = getFirestore(app);

export default db;

import { collection, getDocs, doc, getDoc, updateDoc, serverTimestamp, arrayUnion, setDoc } from 'firebase/firestore';
import db from './firebase';


/**
 *  A classe Storage e responsavel por gerenciar as operacoes de acesso, atualizacao e manipulacao de dados no 
 *  banco de dados Firebase Firestore. Ela abstrai a logica de interacao com o Firestore, proporcionando metodos 
 *  para realizar operacoes como obter todos os usuarios, criar novos usuarios, obter documentos especificos, 
 *  atualizar documentos, entre outros.
 */
export default class Storage {
    collection = collection(db, 'bankapp');
    allUsersDoc = doc(db, 'bankapp', 'allUsersDoc')
    myDoc;

    /**
     *  Inicializa instancias dos objetos Firestore necessarios para operacoes subsequentes
        Aceita um parametro opcional docName para especificar o nome do documento a ser manipulado
     * @param {string} docName 
     */
    constructor(docName) {
        this.myDoc = doc(db, 'bankapp', docName);
    }

    /**
     *  Um metodo generico para acessar o banco de dados, utilizado por outros metodos
        Aceita uma funcao func que representa a operacao a ser realizada
        Retorna os dados se isReturn for verdadeiro, caso contrario, retorna true se a operacao for bem-sucedida
     * @param {function} func 
     * @param {boolean} isReturn 
     * @returns Se a operacao teve sucesso
     */
    accessDatabase = async (func, isReturn = false) => {
        try {
            const docSnapshot = await func();
            if (isReturn) {
                if (docSnapshot.exists()) {
                    const dadosDoDocumento = docSnapshot.data();
                    let jsonD = JSON.stringify(dadosDoDocumento);
                    return JSON.parse(jsonD);
                } else {
                    console.log('Documento nao encontrado!');
                    return 404;
                }
            } else {
                console.log('Operacao efetuada com sucesso!');
                return true;
            }
        } catch (error) {
            console.log('Erro na acao: ', error);
            return false;
        }
    }

    /**
     *  Obtem todos os usuarios do documento especifico (allUsersDoc)
     * @returns Um objeto contendo os dados dos usuarios da base
     */
    async getAllUsers() {
        return await this.accessDatabase(async () => await getDoc(this.allUsersDoc), true);
    }

    /**
     * Adiciona um novo usuario ao array de usuarios no documento especifico (allUsersDoc)
     * @param {object} data 
     * @returns Se a operacao teve sucesso
     */
    async createNewUser(data) {
        const out = await this.accessDatabase(async () => {
            await updateDoc(this.allUsersDoc, {
                users: arrayUnion(data)
            });
        });
        return out;
    }

    /**
     * Cria um novo documento com nome especifico baseado no email do usuario.
     * @param {string} email 
     * @returns Se a operacao teve sucesso
     */
    async createNewUserDoc(email) {
        let newDoc = {
            userData: {
                email: email
            }
        };

        try {
            const novoDocRef = doc(db, 'bankapp', email);
            await setDoc(novoDocRef, { ...newDoc, createdAt: serverTimestamp() });
            console.log('Novo documento criado com nome especifico com sucesso!');
            return true;
        } catch (error) {
            console.log('Erro ao criar novo documento com nome especifico:', error);
            return false;
        }
    }

    /**
     * Obtem todos os documentos na colecao bankapp.
     * @returns Uma lista com os documentos
     */
    async getAllDocs() {
        try {
            const querySnapshot = await getDocs(this.collection);
            const nomesDosDocs = querySnapshot.docs.map((doc) => doc.id);
            return nomesDosDocs;
        } catch (error) {
            console.error('Erro ao obter nomes dos documentos:', error);
            return [];
        }
    }

    /**
     * Obtem todos os dados do documento especifico (myDoc)
     * @returns Os dados do documento
     */
    async getFullDoc() {
        return await this.accessDatabase(async () => await getDoc(this.myDoc), true);
    }

    /**
     * Atualiza o documento especifico (myDoc)
     * @param {object} data 
     * @returns Se a operacao teve sucesso
     */
    async updateDoc(data) {
        const out = await this.accessDatabase(async () => {
            await updateDoc(this.myDoc, { ...data, updatedAt: serverTimestamp() });
        });
        return out;
    }

    /**
     * Adiciona um investimento ao valor ja existente no banco
     * @param {object} data 
     * @returns Se a operacao teve sucesso
     */
    async appendToInvestments(data) {
        const out = await this.accessDatabase(async () => {
            await updateDoc(this.myDoc, {
                investments: arrayUnion(data)
            });
        });
        return out;
    }

    /**
     * Adiciona uma receita ao valor ja existente no banco
     * @param {object} data 
     * @returns Se a operacao teve sucesso
     */
    async appendToIncomes(data) {
        const out = await this.accessDatabase(async () => {
            await updateDoc(this.myDoc, {
                incomes: arrayUnion(data)
            });
        });
        return out;
    }

    /**
     * Adiciona um gasto/uma compra ao valor ja existente no banco
     * @param {object} data 
     * @returns Se a operacao teve sucesso
     */
    async appendToExpenses(data) {
        const out = await this.accessDatabase(async () => {
            await updateDoc(this.myDoc, {
                expenses: arrayUnion(data)
            });
        });
        return out;
    }

    /**
     * Deleta um documento especifico da colecao
     * @param {number} index
     * @returns Se a operacao teve sucesso
     */
    async deleteContent(index) {
        try {
            this.collection.doc(index).delete();
            return 'Dados deletados com sucesso!';
        } catch (e) {
            return 'Ocorreu um problema ao deletar os dados da nuvem!\n' + e;
        }
    }
}

import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { LoadingProvider } from './Context';


import Home from './screens/Home';
import Profile from './screens/Profile';
import Settings from './screens/Settings';
import Investments from './screens/subscreens/Investments'
import SaveInvestments from './screens/subscreens/SaveInvestment';

import AuthScreen from './screens/AuthScreen';
import Graphs from './screens/Graphs';
import Money from './screens/Money';
import SplashScreen from './screens/subscreens/SplashScreen';

const Stack = createStackNavigator();


export default function Main() {


    let opt = {
        headerStyle: {
            backgroundColor: 'transparent',
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
            color: '#fff',
        },
        headerShown: false
    }


    return (
        <NavigationContainer>
            <LoadingProvider>
                <Stack.Navigator initialRouteName="Auth" screenOptions={{
                    headerShown: false
                }}>
                    <Stack.Screen options={opt} name="Auth" component={AuthScreen} />
                    <Stack.Screen options={opt} name="Home" component={Home} />
                    <Stack.Screen options={opt} name="Profile" component={Profile} />
                    <Stack.Screen options={opt} name="Settings" component={Settings} />
                    <Stack.Screen options={opt} name="Investments" component={Investments} />
                    <Stack.Screen options={opt} name="SaveInvestments" component={SaveInvestments} />
                    <Stack.Screen options={opt} name="Graphs" component={Graphs} />
                    <Stack.Screen options={opt} name="Money" component={Money} />
                    <Stack.Screen options={opt} name="Splash" component={SplashScreen} />
                </Stack.Navigator>
            </LoadingProvider>
        </NavigationContainer>
    );
}

import React, { useEffect, useState } from 'react';
import { View, Text, TextInput, StatusBar, StyleSheet, Image, TouchableOpacity, KeyboardAvoidingView, ScrollView, Keyboard } from 'react-native';
import { useLoading } from '../Context';
import { Foundation } from '@expo/vector-icons';
import Storage from '../database/firebaseMethods';
import { Ionicons } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';

const statusBarHeight = StatusBar.currentHeight ? StatusBar.currentHeight + 80 : 200

export default function AuthScreen({ navigation }) {
    const { showLoading, hideLoading } = useLoading();
    const fr = new Storage("allUsersDoc");

    //#region UseState
    const [allUsers, setAllUsers] = useState([]);

    const [signUpMessage, setSignUpMessage] = useState("");
    const [msgDisp, setMsgDisp] = useState(false)
    const [resetElement, setResetElement] = useState(false)
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [password2, setPassword2] = useState('');

    const [showPassword, setShowPassword] = useState(false);
    const [showPassword2, setShowPassword2] = useState(false);
    const [icon, setIcon] = useState("eye-off");
    const [icon2, setIcon2] = useState("eye-off");

    const [isSingUp, setIsSignUp] = useState(false);

    //#endregion

    useEffect(() => {
        const getUsers = async () => {
            // await AsyncStorage.removeItem('isAuth');
            // await AsyncStorage.removeItem('fullUserData');
            // await AsyncStorage.removeItem('userDoc');
            const auth = await AsyncStorage.getItem('isAuth');

            if (auth === "true") {
                navigation.navigate("Splash");
            } else {
                try {
                    showLoading();
                    var docs = await fr.getAllUsers();
                    setAllUsers(docs.users);
                    hideLoading();
                } catch (err) { console.log(err) } finally { hideLoading(); }
            }
        };
        getUsers();
    }, [])

    const clearFields = (clearMsg = true) => {
        setIsSignUp(false);
        if (clearMsg) {
            setMsgDisp(false);
            setSignUpMessage("");
        }
        setEmail("");
        setPassword("");
        setPassword2("");
        setResetElement(false);
        setShowPassword(false);
        setShowPassword2(false);
        setIcon("eye-off");
        setIcon2("eye-off");
    }

    const handleSignUp = async () => {
        Keyboard.dismiss();
        if (password === "" || email === "") {
            setSignUpMessage("O email/senha não podem estar vazios!");
            setMsgDisp(true);
        } else {
            setIsSignUp(true);
            if (password === password2) {
                try {
                    showLoading();
                    let shouldRegister = true;
                    if (allUsers != undefined) {
                        allUsers.forEach(element => {
                            if (element.email === email) {
                                shouldRegister = false;
                                setSignUpMessage("Email ja cadastrado!");
                            }
                        });
                    }

                    if (shouldRegister) {
                        await fr.createNewUser({
                            email: email,
                            pass: password
                        });

                        await fr.createNewUserDoc(email);
                        await fr.updateDoc({
                            userData: {
                                email: email
                            }
                        })
                        await AsyncStorage.setItem('isAuth', "true");
                        await AsyncStorage.setItem('isNew', "true");
                        await AsyncStorage.setItem('userDoc', email);
                        clearFields();
                        navigation.navigate('Splash');
                    }

                } catch (error) {
                    setSignUpMessage("Houve um problema no cadastro");
                    setMsgDisp(true);
                    console.log('Erro no cadastro:', error.message);
                } finally {
                    hideLoading();
                }
            } else {
                setSignUpMessage("As senhas são diferentes!");
                setMsgDisp(true);
            }
        }
    };

    const handleSignIn = async () => {
        Keyboard.dismiss();
        if (!email || email.trim() === "") {
            setSignUpMessage("O email nao pode estar vazio!");
            setMsgDisp(true);
        } else if (!password || password.trim() === "") {
            setSignUpMessage("A senha nao pode estar vazia!");
            setMsgDisp(true);
        } else {
            try {
                const userExists = allUsers.find(param => param.email === email);
                if (!userExists) {
                    setSignUpMessage("Email nao encontrado");
                    setMsgDisp(true);
                    clearFields(false);
                } else {
                    if (userExists.pass === password) {
                        await AsyncStorage.setItem('userDoc', email);
                        clearFields();
                        await AsyncStorage.setItem('isAuth', "true");
                        navigation.navigate('Splash');
                    } else {
                        setSignUpMessage("Senha incorreta");
                        setResetElement(true);
                    }
                }

            } catch (error) {
                console.error('Erro no login:', error.message);
            }
        }

    };


    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
        if (showPassword) {
            setIcon("eye-off");
        } else {
            setIcon("eye");
        }
    };

    const togglePasswordVisibility2 = () => {
        setShowPassword2(!showPassword2);
        if (showPassword2) {
            setIcon2("eye-off");
        } else {
            setIcon2("eye");
        }
    };


    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
            style={{ flex: 1 }}
        >
            <ScrollView style={{ marginTop: statusBarHeight }}>
                <View style={{ justifyContent: 'center', flexDirection: 'row', marginTop: 32 }}>
                    <Image style={{ width: 100, height: 100 }} source={require('./../static/imgs/logo.png')} />
                </View>
                <View style={{ marginHorizontal: 10 }}>
                    <View style={{ marginVertical: 10 }}>
                        <Text style={styles.label}>Email:</Text>
                        <TextInput style={styles.input}
                            placeholder="Digite seu email"
                            onChangeText={(text) => setEmail(text)}
                            value={email}
                            autoCapitalize="none"
                            keyboardType="email-address"
                        />
                        <Text style={styles.label}>Senha:</Text>
                        <View>
                            <TouchableOpacity onPress={togglePasswordVisibility} style={styles.eye}>
                                <Ionicons name={icon} size={34} color="darkblue" />
                            </TouchableOpacity>
                            <TextInput style={styles.input}
                                placeholder="Digite sua senha"
                                onChangeText={(text) => setPassword(text)}
                                secureTextEntry={!showPassword}
                                value={password}
                                autoCapitalize="none"
                            />
                        </View>
                        {isSingUp ?
                            <View>
                                <TouchableOpacity onPress={togglePasswordVisibility2} style={styles.eye}>
                                    <Ionicons name={icon2} size={34} color="darkblue" />
                                </TouchableOpacity>
                                <TextInput style={styles.input}
                                    placeholder="Confirme sua senha"
                                    onChangeText={(text) => setPassword2(text)}
                                    secureTextEntry={!showPassword2}
                                    value={password2}
                                />
                            </View> : <Text></Text>
                        }
                    </View>
                    <TouchableOpacity style={styles.btn} onPress={handleSignIn}>
                        <Text style={{ textAlign: 'center' }}>Login</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.btn} onPress={handleSignUp}>
                        <Text style={{ textAlign: 'center' }}>Cadastrar</Text>
                    </TouchableOpacity>
                </View>
                {(msgDisp ?
                    <View style={styles.msgDispSt}>
                        <Foundation name="x-circle" size={30} color="#8a0000" style={{ marginRight: 5 }} />
                        <Text style={{ color: '#8a0000', fontSize: 20 }}>{signUpMessage}</Text>
                    </View>

                    : <Text></Text>)}

                {resetElement ? <TouchableOpacity onPress={() => { alert("Que pena :(") }}>
                    <Text style={{ color: 'darkblue', textDecorationLine: 'underline', marginLeft: 16, fontSize: 18, marginTop: 16 }}>
                        Esqueci minha senha</Text>
                </TouchableOpacity> : <Text></Text>}
            </ScrollView >
        </KeyboardAvoidingView>
    );
};

const styles = StyleSheet.create({
    label: {
        fontSize: 24
    },
    input: {
        borderWidth: 1,
        borderColor: 'blue',
        padding: 10,
        fontSize: 18,
        marginBottom: 15
    },
    eye: {
        position: 'absolute',
        bottom: 10,
        right: 12,
        padding: 10,
        zIndex: 99
    },
    btn: {
        width: 200,
        borderWidth: 1,
        borderRadius: 10,
        borderColor: 'darkblue',
        paddingVertical: 12,
        paddingHorizontal: 12,
        justifyContent: 'center',
        alignSelf: 'center',
        marginBottom: 18
    },
    msgDispSt: {
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 24,
        paddingHorizontal: 5,
        flexWrap: 'wrap'
    }
})

import React, { useEffect, useState } from "react";
import { Dimensions, View, StyleSheet, Text, ScrollView, TouchableOpacity, StatusBar } from "react-native";
import { PieChart, BarChart, LineChart } from "react-native-chart-kit";
import FlashMessage, { showMessage } from "react-native-flash-message";
import { Ionicons } from '@expo/vector-icons';

import { useLoading } from "../Context";


const screenWidth = Dimensions.get("window").width;
const statusBarHeight = StatusBar.currentHeight ? StatusBar.currentHeight + 5 : 64;


export default function Graphs({ route, navigation }) {

    const { showLoading, hideLoading } = useLoading();

    const [moneyFlow, setMoneyFlow] = useState([]);
    const [incomeData, setIncomeData] = useState(null);
    const [expensesData, setExpensesData] = useState(null);

    useEffect(() => {

        showLoading();
        let dt = route.params.allData;

        //money flow
        let newFlow = [];
        if (dt.moneyFlow) {
            dt.moneyFlow.forEach(element => {
                newFlow.push({
                    name: element.name === "expenses" ? "Gastos" : "Receitas",
                    value: parseFloat(element.value),
                    color: element.name === "expenses" ? '#990000' : "#008060"
                })
            });
        }
        setMoneyFlow(newFlow);

        //income
        let incomes1 = dt.incomes;
        let newIncomes = {
            labels: [],
            datasets: [
                {
                    data: []
                }
            ]
        };
        if (incomes1) {
            incomes1.forEach(element => {
                newIncomes.labels.push(element.description);
                newIncomes.datasets[0].data.push(element.value);
            });
            setIncomeData(newIncomes);
        }

        //expenses
        let expenses1 = dt.expenses;
        let newExpenses = {
            labels: [],
            datasets: [
                {
                    data: []
                }
            ]
        };
        if (expenses1) {
            expenses1.forEach(element => {
                newExpenses.labels.push(element.description);
                newExpenses.datasets[0].data.push(element.value);
            });
            setExpensesData(newExpenses);
        }

        hideLoading();
    }, [])


    const chartConfig = {
        backgroundGradientFrom: "#fff",
        backgroundGradientFromOpacity: 0,
        backgroundGradientTo: "#fff",
        backgroundGradientToOpacity: 0,
        strokeWidth: 1,
        barPercentage: 0.5,
    };

    const lineChartConfig = {
        ...chartConfig,
        color: (opacity = 1) => `rgba(139, 16, 0, ${opacity})`
    }

    const barChartConfig = {
        ...chartConfig,
        color: (opacity = 1) => `rgba(0, 139, 93, ${opacity})`
    };


    return (
        <View style={{ marginTop: statusBarHeight, height: '100%' }}>
            <TouchableOpacity style={styles.back} onPress={() => { navigation.navigate("Home") }}>
                <Ionicons name="arrow-back-circle-outline" size={30} color="darkblue" />
            </TouchableOpacity>
            <Text style={[styles.title, { marginTop: 8, paddingHorizontal: 8 }]}>Minhas Finanças</Text>

            <ScrollView style={{ marginBottom: statusBarHeight + 20 }}>

                <View style={styles.graphGroup}>
                    <Text style={styles.graphTitle}>Gastos X Receitas</Text>
                    {moneyFlow ?
                        <PieChart
                            data={moneyFlow}
                            width={screenWidth - 20}
                            height={220}
                            chartConfig={{
                                ...chartConfig,
                                color: () => `rgba(0, 0, 139, 1)`
                            }}
                            accessor={"value"}
                            backgroundColor={"transparent"}
                        />
                        : <Text style={{ margin: 20 }}>Sem dados</Text>
                    }
                </View>

                <View style={styles.graphGroup}>
                    <Text style={styles.graphTitle}>Receitas</Text>
                    {incomeData && incomeData.length > 0 ?
                        <BarChart
                            width={screenWidth}
                            height={300}
                            data={incomeData}
                            yAxisLabel="R$"
                            yLabelsOffset={0}
                            chartConfig={barChartConfig}
                            fromZero={true}
                        />
                        : <Text style={{ margin: 20 }}>Sem dados</Text>
                    }
                </View>

                <View style={styles.graphGroup}>
                    <Text style={styles.graphTitle}>Gastos</Text>
                    <FlashMessage duration={5000} />
                    {expensesData && expensesData.length > 0 ?
                        <LineChart
                            bezier
                            width={screenWidth - 20}
                            height={300}
                            data={expensesData}
                            yAxisLabel="R$"
                            yAxisSuffix=""
                            verticalLabelRotation={20}
                            chartConfig={lineChartConfig}
                            fromZero={true}
                            onDataPointClick={({ value, index, getColor }) => {
                                showMessage({
                                    message: `R$ ${value}`,
                                    description: `${expensesData.labels[index]}`,
                                    backgroundColor: getColor(.5)
                                })
                            }
                            }
                        />
                        : <Text style={{ margin: 20 }}>Sem dados</Text>
                    }
                </View>
            </ScrollView>
        </View>
    )
}


const styles = StyleSheet.create({
    graphTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 16,
        textAlign: 'center'
    },
    graph: {
        fontSize: 16,
        textAlign: 'center'
    },
    graphGroup: {
        borderWidth: 1,
        borderColor: 'rgba(0, 0, 139,.2)',
        margin: 10,
        padding: 5
    },
    back: {
        marginHorizontal: 10,
        marginVertical: 8,
        position: 'absolute',
        top: 6,
        zIndex: 99
    },
    title: {
        fontSize: 28,
        fontWeight: "bold",
        textAlign: "center"
    },
})

import React, { useState, useEffect, useRef } from 'react';
import { StyleSheet, Text, TouchableOpacity, View, Animated, ScrollView, StatusBar, Platform } from 'react-native';
import * as Animatable from 'react-native-animatable';
import { Feather } from '@expo/vector-icons';
import AsyncStorage from '@react-native-async-storage/async-storage';
import HorizontalMenu from './../components/HorizontalMenu';
import Movements from '../components/Movements';
import { useLoading } from '../Context';
import Storage from './../database/firebaseMethods';
import Tooltip from 'react-native-walkthrough-tooltip';


const menuOptions = [
  {
    actionType: 'Adicionando',
    label: 'Entrada',
    icon: 'plussquareo',
    type: 0, //entrada
  },
  {
    actionType: 'Adicionando',
    label: 'Compra',
    icon: 'tagso',
    type: 1, //saida
  },
  {
    actionType: 'Adicionando',
    label: 'Gasto',
    icon: 'barcode',
    type: 1, //saida
  },
  {
    actionType: null,
    label: 'Conta',
    icon: 'setting',
    type: 2 //settings
  },
  {
    actionType: null,
    label: 'Gráficos',
    icon: 'areachart',
    type: 3 //graphs
  },
  {
    actionType: null,
    label: 'Moedas',
    icon: 'money-symbol',
    type: 4 //money
  },
];

const statusBarHeight = StatusBar.currentHeight ? StatusBar.currentHeight + 5 : 64

export default function Home({ navigation }) {
  const { showLoading, hideLoading, getShouldRefresh, setShouldRefresh } = useLoading();
  const [moneyGraph, SetMoneyGraph] = useState([{}]);

  const refresh = async () => {
    const doc = await AsyncStorage.getItem('userDoc');
    const fr = new Storage(doc);
    let res = await fr.getFullDoc();
    await AsyncStorage.setItem('fullUserData', JSON.stringify(res))
  };

  const slideDownRef = useRef(null);
  const [userData, setUserData] = useState({});

  const [money, setMoney] = useState([]);
  const [income, setIncome] = useState(0);
  const [negative, setNegative] = useState(0);

  const [msg, setMsg] = useState('');

  //#region Balance animation
  const fadeAnim = useRef(new Animated.Value(1)).current;

  const fadeIn = () => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 400,
      useNativeDriver: true,
    }).start();
  };

  const fadeOut = () => {
    Animated.timing(fadeAnim, {
      toValue: 0,
      duration: 500,
      useNativeDriver: true,
    }).start();
  };


  const toggleBalance = () => {
    let state = fadeAnim.addListener((st) => { return st })
    if (state % 2 == 0) {
      fadeOut();
    } else {
      fadeIn();
    }
  };

  //#endregion

  const parseDate = (dateString) => {
    if (dateString) {
      const [day, month, year] = dateString.split('/');
      return new Date(`${year}-${month}-${day}`);
    } else {
      return new Date();
    }
  };


  const [showBalanceTip, setBalanceTip] = useState(false);
  const [showMenuTip, setShowMenuTip] = useState(false);
  const [showMovementsTip, setShowMovementsTip] = useState(false);

  useEffect(() => {
    slideDownRef.current.slideInDown(1000);

    let res;
    const getAllData = async () => {
      let isNew = await AsyncStorage.getItem('isNew');

      if (isNew === 'true') {
        setBalanceTip(true);
        await AsyncStorage.removeItem('isNew');
      };

      try {
        showLoading();
        const fullRes = await AsyncStorage.getItem('fullUserData');
        res = JSON.parse(fullRes);

        if (res) {
          setUserData(res.userData);

          let allIncomes = [];
          let incomeSum = 0;
          if (res.incomes) {
            res.incomes.forEach(element => {
              allIncomes.push({
                ...element,
                type: 1
              })
              incomeSum += parseFloat(element.value);
            });
          }
          setIncome(incomeSum.toFixed(2));

          let allExpenses = [];
          let expenseSum = 0;
          if (res.expenses) {
            res.expenses.forEach(element => {
              allExpenses.push({
                ...element,
                type: 0
              })
              expenseSum += parseFloat(element.value);
            });
          }
          setNegative(expenseSum.toFixed(2));

          let allMoney = [...allIncomes, ...allExpenses];

          SetMoneyGraph({
            incomes: allIncomes,
            expenses: allExpenses,
            moneyFlow: [
              {
                name: "incomes",
                value: incomeSum.toFixed(2)
              },
              {
                name: "expenses",
                value: expenseSum.toFixed(2)
              }
            ],
          })

          const listaOrdenada = allMoney.sort((a, b) => {
            const dataA = parseDate(a.date);
            const dataB = parseDate(b.date);
            return dataB - dataA;
          });

          setMoney(listaOrdenada);
          setMsg("")
        } else {
          setMsg('Nenhum dado encontrado!');
        }
      } catch (err) {
        console.log(err);
        setMsg('Nenhum dado encontrado!');
      } finally {
        setShouldRefresh(false);
        hideLoading();
      }
    };


    getAllData().catch(console.error);

  }, [getShouldRefresh()]);


  return (
    <ScrollView style={{ marginTop: statusBarHeight, flex: 1 }}>
      {/** #region HEADER **/}
      <View style={st.header_container}>
        <View style={st.header_content}>
          <Animatable.View ref={slideDownRef} style={st.header_profile}>
            <Text style={st.header_userName}>{userData.name}</Text>

            <TouchableOpacity
              activeOpacity={0.9}
              style={st.header_btnUser}
              onPress={() => navigation.navigate('Profile')}>
              <Feather name="user" size={27} color={'darkblue'} />
            </TouchableOpacity>
          </Animatable.View>
        </View>
      </View>
      {/** #endregion HEADER **/}

      {/** #region BALANCE **/}
      <TouchableOpacity
        style={st.balance_viewContainer}
        onPress={toggleBalance}>
        <Tooltip
          isVisible={showBalanceTip}
          content={
            <View style={{ padding: 5 }}>
              <Text>Você pode esconder ou exibir os valores, apenas clicando nele!</Text>
            </View>
          }
          onClose={() => setBalanceTip(false)}
          placement="bottom"
        >
          <Tooltip
            isVisible={showBalanceTip}
            content={
              <View style={{ padding: 5 }}>
                <Text>Aqui estão os balanços que você registrar no app!</Text>
              </View>
            }
            onClose={() => { setBalanceTip(false); setShowMenuTip(true); }}
            placement="top"
          >
            <View style={st.balance_container} >
              <Animated.View style={[st.balance_data, { opacity: fadeAnim }]}>
                <View style={st.balance_item}>
                  <Text style={st.balance_itemTitle}>Saldo</Text>
                  <View style={st.balance_content}>
                    <Text style={st.balance_currency}>R$</Text>
                    <Text style={st.balance_balance}>{income}</Text>
                  </View>
                </View>
                <View style={st.balance_item}>
                  <Text style={st.balance_itemTitle}>Gastos</Text>
                  <View style={st.balance_content}>
                    <Text style={st.balance_currency}>R$</Text>
                    <Text style={st.balance_expenses}>{negative}</Text>
                  </View>
                </View>
              </Animated.View>
            </View>
          </Tooltip>
        </Tooltip>
      </TouchableOpacity>
      {/** #endregion BALANCE **/}

      <Tooltip
        isVisible={showMenuTip}
        content={
          <View style={{ padding: 5 }}>
            <Text>Arraste o menu para o lado, para ver todas as suas opções!</Text>
          </View>
        }
        onClose={() => { setShowMovementsTip(true); setShowMenuTip(false); }}
        placement="bottom"
      >
        <ScrollView
          style={[st.scrollContainer, { paddingRight: 10 }]}
          horizontal={true}
          showsHorizontalScrollIndicator={false}>
          {menuOptions.map((item, index) => (
            <View style={{ height: 100, marginRight: 15 }} key={index}>
              <HorizontalMenu
                refresh={refresh}
                navigation={navigation}
                dados={item}
                key={index}
                allData={moneyGraph}
              />
            </View>
          ))}
        </ScrollView>
      </Tooltip>
      {/** #region MOVIMENTACOES **/}

      <View style={{ padding: 10, marginLeft: 3, marginRight: 3 }}>

        <Tooltip
          isVisible={showMovementsTip}
          content={
            <View style={{ padding: 5 }}>
              <Text>Aqui ficará toda a movimentação financeira que você registrar!</Text>
            </View>
          }
          onClose={() => { setShowMovementsTip(false); }}
          placement="top"
        >
          <Text style={st.title}>Últimas movimentações</Text>
          <Text>{msg}</Text>
        </Tooltip>
        <ScrollView showsVerticalScrollIndicator={false}>
          {money.map((item, index) => (
            <View key={index}>
              <Movements props={item} key={index} />
            </View>
          ))}
        </ScrollView>
      </View>

      {/** #endregion MOVIMENTACOES **/}
    </ScrollView>
  );
}

